package day04_İncrementDecrement;

public class C05_İncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int sayi1 = 10 ;
	System.out.println(sayi1+=5);
	System.out.println(sayi1);
	
	System.out.println(sayi1-=4);
	
	System.out.println(sayi1/=1);
		
	System.out.println(sayi1*=3);
	
	System.out.println(sayi1+8);
		
	
	System.out.println(sayi1);
	sayi1++;
	
	System.out.println(sayi1);
	
	sayi1--;
	
	System.out.println(sayi1);
	
	
	
	
	
	
	
	
	
	
	
	
	
	
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
