package day10_ternary_switch;

public class C10_SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sayi=10;
		switch(sayi) {
		
		case 10:
			System.out.println("Ikı basamaklı en kuccuk sayı");
		
		break;
			
		case 100:
			System.out.println("Uc basamaklı en kuccuk sayı");
		
			break;
		
		case 1000:
			System.out.println("Dort basamaklı en kuccuk sayı");
		
			break;
		
		default:
			System.out.println("girdigin sayıyı degistir salak");
		
		
		
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
